$.rails.allowAction=function(i){return i.data("confirm")==undefined||($.rails.showConfirmationDialog(i),!1)},$.rails.confirmed=function(i){i.data("confirm",null),i.trigger("click.rails")},$.rails.showConfirmationDialog=function(i){var o=i.data("confirm");$.ajax({url:"/render_confirmation_modal",success:function(a){var n=a.html;$(n).dialog({resizable:!1,dialogClass:"jquery-modal",modal:!0}),$("#confirmation-text").text(o),$("#modal-confirm").click(function(){$.rails.confirmed(i)}),$("#modal-cancel").click(function(){$(".ui-dialog").remove(),$(".ui-widget-overlay").remove()})}})};